<template>
    <div class="button" @click="onClick"
         v-bind:class="{ button1: state}">
        <img height="30" width="30" :src="currentStatusImage" @mouseenter="onMouseEnter" @mouseleave="onMouseLeave"/>
    </div>
</template>

<script>
    export default {
        name: "StatusButton",
        beforeMount: function () {
            this.currentStatusImage = this.statusImage1;
        },
        data() {
            return {
                currentStatusImage: null,
                statusImage1: "data:image/png;base64,",
                statusImage2: "data:image/png;base64,",
                state: false
            }
        },
        methods: {
            onMouseEnter() {
                this.currentStatusImage = this.statusImage2;
            },
            onMouseLeave() {
                this.currentStatusImage = this.statusImage1;
            }
            ,
            onClick() {
                this.state = !this.state;
                this.$emit('statuschanged', this.state);
            }
        }
    }
</script>

<style scoped>

    .button {
        display: inline;
        cursor: pointer;
        color: #2B2B36;
    }

    .button1 {
        transform: rotate(180deg);
        -ms-transform: rotate(180deg); /* IE 9 */
        -webkit-transform: rotate(180deg); /* Safari and Chrome */
    }
</style>